mapper.insert:
		INSERT INTO TB_USER (
        ID, NAME, EMAIL, PASSWORD, SCHOOL, GENDER, GRADE, PHONE_NUMBER
    )
    VALUES (
        #{id}, #{name}, #{email}, #{password}, #{school}, #{gender}, #{grade}, #{phone_number}
    )

mapper.select:
	SELECT * FROM TB_USER
	WHERE ID = #{id}
  	AND NAME = #{name}
  	AND EMAIL = #{email}
 	AND PHONE_NUMBER = #{phone_number}

mapper.update:
UPDATE TB_USER
SET PASSWORD = #{password}
WHERE ID = #{id}
